import React, { useEffect } from 'react';
import { NavigationContainer } from '@react-navigation/native';
import { createNativeStackNavigator } from '@react-navigation/native-stack';
import { Platform } from 'react-native';

// Import utility managers
import StorageManager from './src/utils/StorageManager';
import ConnectivityManager from './src/utils/ConnectivityManager';

// Import screens
import HomeScreen from './src/components/HomeScreen';
import MergePDF from './src/components/PDFTools/MergePDF';
import SplitPDF from './src/components/PDFTools/SplitPDF';
import CompressPDF from './src/components/PDFTools/CompressPDF';

const Stack = createNativeStackNavigator();

export default function App() {
  useEffect(() => {
    // Initialize connectivity checking
    ConnectivityManager.checkConnectivity();

    // Add connectivity listener
    const handleConnectivityChange = (isOnline) => {
      console.log('Connection status:', isOnline ? 'online' : 'offline');
    };

    ConnectivityManager.addListener(handleConnectivityChange);

    return () => {
      ConnectivityManager.removeListener(handleConnectivityChange);
    };
  }, []);

  return (
    <NavigationContainer>
      <Stack.Navigator
        screenOptions={{
          headerStyle: {
            backgroundColor: '#f14444',
          },
          headerTintColor: '#fff',
          headerTitleStyle: {
            fontWeight: 'bold',
          },
          animation: Platform.OS === 'web' ? 'none' : 'default',
        }}
      >
        <Stack.Screen 
          name="Home" 
          component={HomeScreen}
          options={{
            title: 'PDF Tools',
          }}
        />
        <Stack.Screen 
          name="MergePDF" 
          component={MergePDF}
          options={{
            title: 'Merge PDF Files',
          }}
        />
        <Stack.Screen 
          name="SplitPDF" 
          component={SplitPDF}
          options={{
            title: 'Split PDF',
          }}
        />
        <Stack.Screen 
          name="CompressPDF" 
          component={CompressPDF}
          options={{
            title: 'Compress PDF',
          }}
        />
      </Stack.Navigator>
    </NavigationContainer>
  );
}
