import React from 'react';
import { TouchableOpacity, Text, ActivityIndicator } from 'react-native';

const Button = ({ 
  onPress, 
  disabled = false, 
  loading = false,
  variant = 'primary',
  style = {},
  textStyle = {},
  children 
}) => {
  const getStyles = () => {
    const baseStyle = {
      padding: 15,
      borderRadius: 8,
      alignItems: 'center',
      justifyContent: 'center',
      flexDirection: 'row',
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.1,
      shadowRadius: 4,
      elevation: 3,
    };

    if (disabled || loading) {
      return {
        ...baseStyle,
        backgroundColor: '#cbd5e0',
      };
    }

    switch (variant) {
      case 'secondary':
        return {
          ...baseStyle,
          backgroundColor: '#fff',
          borderWidth: 1,
          borderColor: '#e2e8f0',
        };
      case 'danger':
        return {
          ...baseStyle,
          backgroundColor: '#ef4444',
        };
      default:
        return {
          ...baseStyle,
          backgroundColor: '#f14444',
        };
    }
  };

  const getTextColor = () => {
    if (disabled || loading) return '#718096';
    return variant === 'secondary' ? '#2d3748' : '#fff';
  };

  return (
    <TouchableOpacity
      onPress={onPress}
      disabled={disabled || loading}
      style={{
        ...getStyles(),
        ...style,
      }}
    >
      {loading && (
        <ActivityIndicator 
          size="small" 
          color={variant === 'secondary' ? '#f14444' : '#fff'} 
          style={{ marginRight: 8 }}
        />
      )}
      <Text
        style={{
          color: getTextColor(),
          fontSize: 16,
          fontWeight: '600',
          ...textStyle,
        }}
      >
        {children}
      </Text>
    </TouchableOpacity>
  );
};

export default Button;
