import React from 'react';
import { View, Text, ScrollView, TouchableOpacity, Platform } from 'react-native';

const ToolCard = ({ title, icon, description, onPress }) => (
  <TouchableOpacity
    onPress={onPress}
    style={{
      backgroundColor: 'white',
      borderRadius: 12,
      padding: 20,
      marginBottom: 16,
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 2 },
      shadowOpacity: 0.1,
      shadowRadius: 4,
      elevation: 3,
    }}
  >
    <View style={{ flexDirection: 'row', alignItems: 'center', marginBottom: 10 }}>
      {Platform.OS === 'web' ? (
        <i className={`fas ${icon} text-2xl text-pdf-red`} style={{ marginRight: 12 }} />
      ) : (
        <Text style={{ marginRight: 12, fontSize: 24, color: '#f14444' }}>ðŸ“„</Text>
      )}
      <Text style={{ fontSize: 18, fontWeight: '600', color: '#2d3748' }}>{title}</Text>
    </View>
    <Text style={{ color: '#718096', fontSize: 14 }}>{description}</Text>
  </TouchableOpacity>
);

const HomeScreen = ({ navigation }) => {
  const tools = [
    {
      title: 'Merge PDF',
      icon: 'fa-object-group',
      description: 'Combine multiple PDF files into one document',
      route: 'MergePDF'
    },
    {
      title: 'Split PDF',
      icon: 'fa-cut',
      description: 'Extract pages from a PDF into a new file',
      route: 'SplitPDF'
    },
    {
      title: 'Compress PDF',
      icon: 'fa-compress',
      description: 'Reduce PDF file size while preserving quality',
      route: 'CompressPDF'
    },
    {
      title: 'PDF to Word',
      icon: 'fa-file-word',
      description: 'Convert PDF to editable Word document',
      route: 'PDFToWord'
    },
    {
      title: 'PDF to Excel',
      icon: 'fa-file-excel',
      description: 'Extract PDF tables to Excel spreadsheet',
      route: 'PDFToExcel'
    },
    {
      title: 'PDF to PowerPoint',
      icon: 'fa-file-powerpoint',
      description: 'Convert PDF to PowerPoint presentation',
      route: 'PDFToPowerPoint'
    },
    {
      title: 'Edit PDF',
      icon: 'fa-pen-to-square',
      description: 'Add text, images, and annotations to PDF',
      route: 'EditPDF'
    },
    {
      title: 'Sign PDF',
      icon: 'fa-signature',
      description: 'Add electronic signatures to documents',
      route: 'SignPDF'
    },
    {
      title: 'Protect PDF',
      icon: 'fa-lock',
      description: 'Encrypt and password-protect your PDFs',
      route: 'ProtectPDF'
    }
  ];

  return (
    <ScrollView 
      style={{ flex: 1, backgroundColor: '#f7fafc' }}
      contentContainerStyle={{ padding: 16 }}
    >
      <View style={{ marginBottom: 24 }}>
        <Text style={{ fontSize: 28, fontWeight: 'bold', color: '#2d3748', marginBottom: 8 }}>
          PDF Tools
        </Text>
        <Text style={{ fontSize: 16, color: '#718096' }}>
          Privacy-focused PDF utilities that work offline
        </Text>
      </View>

      <View style={{ marginBottom: 24 }}>
        <Text style={{ fontSize: 20, fontWeight: '600', color: '#2d3748', marginBottom: 16 }}>
          Popular Tools
        </Text>
        {tools.map((tool, index) => (
          <ToolCard
            key={index}
            title={tool.title}
            icon={tool.icon}
            description={tool.description}
            onPress={() => navigation.navigate(tool.route)}
          />
        ))}
      </View>

      {Platform.OS === 'web' && (
        <View style={{ padding: 16, backgroundColor: '#fff', borderRadius: 12, marginBottom: 24 }}>
          <Text style={{ fontSize: 16, color: '#4a5568', textAlign: 'center' }}>
            ðŸ’¡ Tip: All tools work offline! Your files never leave your device.
          </Text>
        </View>
      )}
    </ScrollView>
  );
};

export default HomeScreen;
