import React from 'react';
import { View, Text } from 'react-native';

const ErrorMessage = ({ 
  message, 
  style = {},
  textStyle = {},
}) => {
  if (!message) return null;

  return (
    <View style={{ 
      backgroundColor: '#fee2e2',
      padding: 12,
      borderRadius: 8,
      borderWidth: 1,
      borderColor: '#fecaca',
      marginBottom: 16,
      ...style,
    }}>
      <View style={{ flexDirection: 'row', alignItems: 'center' }}>
        {/* Error icon for web */}
        {Platform.OS === 'web' && (
          <i 
            className="fas fa-exclamation-circle text-red-500 mr-2"
            style={{ color: '#dc2626', marginRight: 8 }}
          />
        )}
        <Text style={{ 
          color: '#dc2626',
          fontSize: 14,
          flex: 1,
          ...textStyle,
        }}>
          {message}
        </Text>
      </View>
    </View>
  );
};

export default ErrorMessage;
