import React from 'react';
import { View, Text } from 'react-native';

const Card = ({ 
  title,
  subtitle,
  children,
  style = {},
  titleStyle = {},
  subtitleStyle = {},
  contentStyle = {},
}) => {
  return (
    <View style={{ 
      backgroundColor: '#fff',
      padding: 16,
      borderRadius: 12,
      marginBottom: 20,
      shadowColor: '#000',
      shadowOffset: { width: 0, height: 1 },
      shadowOpacity: 0.1,
      shadowRadius: 3,
      elevation: 2,
      ...style,
    }}>
      {title && (
        <Text style={{ 
          fontSize: 16, 
          color: '#2d3748', 
          marginBottom: subtitle ? 4 : 8,
          fontWeight: '500',
          ...titleStyle,
        }}>
          {title}
        </Text>
      )}

      {subtitle && (
        <Text style={{ 
          fontSize: 14, 
          color: '#718096', 
          marginBottom: 16,
          ...subtitleStyle,
        }}>
          {subtitle}
        </Text>
      )}

      <View style={contentStyle}>
        {children}
      </View>
    </View>
  );
};

// Helper component for tips/info boxes
Card.Info = ({ children, style = {} }) => (
  <View style={{ 
    marginTop: 24,
    padding: 16,
    backgroundColor: '#fff',
    borderRadius: 12,
    borderWidth: 1,
    borderColor: '#e2e8f0',
    ...style,
  }}>
    <Text style={{ 
      fontSize: 14, 
      color: '#718096', 
      textAlign: 'center',
      lineHeight: 20,
    }}>
      {Platform.OS === 'web' && 'ðŸ’¡ '}
      {children}
    </Text>
  </View>
);

// Helper component for bullet points
Card.BulletPoint = ({ children, style = {} }) => (
  <View style={{ 
    flexDirection: 'row', 
    alignItems: 'flex-start',
    marginBottom: 4,
    ...style,
  }}>
    <Text style={{ 
      color: '#718096',
      marginRight: 8,
      marginTop: -2,
    }}>
      â€¢
    </Text>
    <Text style={{ 
      flex: 1,
      fontSize: 14,
      color: '#718096',
      lineHeight: 20,
    }}>
      {children}
    </Text>
  </View>
);

// Helper component for file details
Card.FileDetails = ({ 
  fileName, 
  fileSize, 
  additionalDetails = null,
  style = {} 
}) => (
  <View style={style}>
    <Text style={{ 
      fontSize: 16, 
      color: '#2d3748', 
      marginBottom: 4,
      fontWeight: '500',
    }}>
      {fileName}
    </Text>
    <Text style={{ 
      fontSize: 14, 
      color: '#718096',
      marginBottom: additionalDetails ? 4 : 0,
    }}>
      {fileSize}
    </Text>
    {additionalDetails && (
      <Text style={{ fontSize: 14, color: '#718096' }}>
        {additionalDetails}
      </Text>
    )}
  </View>
);

export default Card;
