import React from 'react';
import { View, Text, Platform } from 'react-native';
import Button from './Button';

const FileInput = ({
  onFileSelect,
  accept = '.pdf',
  multiple = false,
  buttonText = 'Select File',
  buttonVariant = 'primary',
  style = {},
}) => {
  // Web-specific file input
  const WebFileInput = Platform.OS === 'web' ? (
    <input
      type="file"
      accept={accept}
      multiple={multiple}
      onChange={onFileSelect}
      className="hidden"
      id="file-input"
      style={{ display: 'none' }}
    />
  ) : null;

  const handlePress = () => {
    if (Platform.OS === 'web') {
      document.getElementById('file-input').click();
    } else {
      // We'll implement Android file picking later
    }
  };

  return (
    <View style={style}>
      {Platform.OS === 'web' ? (
        <>
          <Button
            onPress={handlePress}
            variant={buttonVariant}
          >
            {buttonText}
          </Button>
          {WebFileInput}
        </>
      ) : (
        <View style={{ 
          padding: 16,
          backgroundColor: '#f8fafc',
          borderRadius: 8,
          borderWidth: 1,
          borderColor: '#e2e8f0',
          alignItems: 'center'
        }}>
          <Text style={{ color: '#718096', marginBottom: 8 }}>
            Android file picker coming soon
          </Text>
        </View>
      )}
    </View>
  );
};

export default FileInput;
